INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (31, 31, 1995.00, 5, 'ACID Saddle Bag PRO S', 'Cette sacoche de selle ACID PRO S est l''accessoire indispensable pour emporter vos outils essentiels sans vous encombrer. Fabriquée dans un matériau résistant et déperlant, elle garde vos affaires au sec même par temps humide. Sa fixation par velcro ultra-rapide s''adapte à la majorité des rails de selle, garantissant un maintien stable et silencieux durant vos sorties.', 'Sacoche compacte et résistante à l''eau.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (32, 32, 5995.00, 10, 'ACID Carrier Cargo Rilink', 'Transformez votre vélo en véritable utilitaire avec ce porte-bagages arrière en aluminium haute qualité. Compatible avec le système innovant RILink, il permet de clipser et déclipser paniers et sacoches compatibles en une seconde. Sa structure légère mais robuste est conçue pour supporter des charges importantes, idéale pour le cyclotourisme ou les courses quotidiennes.', 'Structure en aluminium légère et très robuste.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (36, 36, 5495.00, 19, 'ACID Light Set PRO 40', 'Voyez et soyez vu avec le kit d''éclairage ACID PRO 40, comprenant un phare avant de 40 Lux et un feu arrière puissant. Rechargeables via USB, ces lampes offrent une autonomie généreuse et se fixent sans outil grâce à des sangles en silicone robustes. Homologué StVZO, ce kit garantit un faisceau lumineux qui n''éblouit pas les autres usagers tout en éclairant parfaitement votre route.', 'Kit complet avant (40 Lux) et arrière.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (37, 37, 3995.00, 20, 'ACID Front Light PRO 60', 'L''éclairage avant ACID PRO 60 délivre une puissance impressionnante de 60 Lux pour illuminer les routes les plus sombres. Son corps compact abrite une batterie lithium-ion rechargeable par USB et dispose d''un indicateur de charge pour ne jamais être pris au dépourvu. Avec plusieurs modes d''intensité, il s''adapte à l''éclairage urbain comme aux routes de campagne non éclairées.', 'Éclairage avant puissant de 60 Lux.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (38, 38, 2495.00, 21, 'ACID Rear Light PRO-E', 'Spécialement conçu pour les vélos à assistance électrique, le feu arrière ACID PRO-E s''intègre directement au système électrique de votre VAE. Il offre une visibilité exceptionnelle grâce à ses LED haute puissance et son large angle de diffusion. Sa conception robuste et étanche assure une fiabilité totale par tous les temps, augmentant considérablement votre sécurité dans le trafic.', 'Feu arrière spécifique pour vélos électriques.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (39, 39, 995.00, 22, 'ACID Mount for Front Light', 'Ce support de guidon déporté est conçu pour positionner votre éclairage avant ACID de manière centrale et optimale. En libérant de l''espace sur votre cintre, il permet une installation plus propre de votre poste de pilotage, laissant la place pour un compteur ou un GPS. Sa construction rigide élimine les vibrations du faisceau lumineux, même sur les routes pavées ou accidentées.', 'Support déporté pour centrer l''éclairage.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (43, 43, 1995.00, 27, 'ACID Mirror View', 'Roulez en toute sécurité avec le rétroviseur ACID Mirror View, qui vous offre une vision claire de ce qui se passe derrière vous sans avoir à vous retourner. Il s''insère facilement dans l''extrémité de votre guidon et peut être replié lors du stationnement pour éviter les chocs. Son miroir convexe anti-reflet garantit un champ de vision élargi, essentiel pour la circulation en milieu urbain.', 'Rétroviseur de guidon ajustable et repliable.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (40, 40, 795.00, 24, 'CUBE Bottle Grip 0.75L', 'Restez hydraté durant vos efforts avec le bidon CUBE Grip d''une capacité de 750 ml. Fabriqué en plastique souple sans BPA, il est facile à presser et ne donne aucun goût à votre boisson. Son bouchon à large ouverture facilite le remplissage et le nettoyage (compatible lave-vaisselle), tandis que l''embout souple est agréable en bouche et parfaitement étanche une fois fermé.', 'Bidon grande capacité 750ml sans BPA.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (41, 41, 995.00, 25, 'ACID Bottle Cage HPP', 'Le porte-bidon ACID HPP combine légèreté et maintien sécurisé grâce à sa construction en polymère haute performance. Son design a été optimisé pour permettre une insertion et un retrait faciles du bidon, même en roulant, tout en garantissant qu''il ne bougera pas sur les terrains chaotiques. Disponible en plusieurs coloris pour s''assortir parfaitement au design de votre cadre.', 'Porte-bidon léger en composite haute performance.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (42, 42, 795.00, 26, 'ACID Bell Pure', 'La sonnette ACID Bell Pure prouve qu''un élément de sécurité peut aussi être élégant. Fabriquée avec un dôme en laiton de haute qualité, elle produit un son clair et puissant qui porte loin, assurant votre signalement aux piétons. Sa fixation compacte et minimaliste s''installe discrètement sur n''importe quel guidon, préservant l''esthétique épurée de votre vélo.', 'Sonnette minimaliste au design épuré.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (44, 44, 2295.00, 28, 'ACID Kickstand FM PURE', 'Garez votre vélo n''importe où avec la béquille ACID FM PURE, conçue pour les vélos équipés de l''interface Flat Mount. Son mécanisme robuste assure une grande stabilité, même avec des bagages chargés. La longueur est facilement ajustable sans outil pour s''adapter parfaitement à la taille de vos roues, et son pied large en plastique évite l''enfoncement dans les sols meubles.', 'Béquille robuste pour interface Flat Mount.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (46, 46, 2495.00, 32, 'ACID Pedals Junior', 'Spécialement dimensionnées pour les petits pieds, les pédales ACID Junior offrent sécurité et confiance aux jeunes cyclistes. Leur surface antidérapante en caoutchouc évite que le pied ne glisse, tandis que les réflecteurs intégrés augmentent la visibilité latérale. Robustes et légères, elles sont parfaites pour équiper les vélos d''enfants de 16 à 24 pouces.', 'Pédales ergonomiques adaptées aux enfants.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (50, 50, 4995.00, 30, 'CUBE Pedals Clipless', 'Améliorez votre efficacité de pédalage avec les pédales automatiques CUBE Clipless. Compatibles avec le standard SPD, elles permettent une connexion solide entre le cycliste et le vélo pour un transfert de puissance optimal. La tension de déclenchement est réglable pour s''adapter à votre niveau, et leur mécanisme ouvert évacue efficacement la boue pour un enclenchement fiable en toutes conditions.', 'Pédales automatiques compatibles système SPD.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (47, 47, 3495.00, 35, 'ACID Mudguard Set TREKKING 45', 'Restez propre et sec avec ce set de garde-boue ACID TREKKING 45, conçu pour épouser parfaitement les roues de 28 pouces. Leur profil enveloppant et leur largeur de 45mm offrent une protection maximale contre les projections d''eau et de boue. Fabriqués en plastique haute résistance, ils ne se déforment pas et se fixent solidement grâce aux tringles en acier inoxydable incluses.', 'Set complet avant et arrière pour Trekking.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (201, 201, 149.95, 150, 'CUBE Helmet ROAD RACE', 'Le casque CUBE ROAD RACE est conçu pour les cyclistes exigeants qui recherchent performance et sécurité. Doté d''une coque aérodynamique et du système de protection MIPS, il réduit les forces rotationnelles en cas d''impact oblique. Ses multiples ventilations assurent un flux d''air constant pour garder la tête froide, même lors des ascensions les plus intenses en plein été.', 'Casque route aérodynamique haute performance.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (203, 203, 349.95, 80, 'Newmen Evolution SL A.30', 'La roue arrière Newmen Evolution SL A.30 est la référence pour l''enduro et le trail agressif. Sa jante en aluminium haute résistance de 30mm de largeur interne offre un support parfait pour les pneus larges, permettant de rouler à basse pression pour plus de grip. Équipée du moyeu Fade silencieux et fiable, elle combine durabilité exceptionnelle et poids contenu pour grimper aussi bien qu''elle descend.', 'Roue arrière VTT 29" ultra-résistante.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (204, 204, 699.95, 81, 'Newmen Advanced SL R.50', 'Coupez le vent avec la roue avant Newmen Advanced SL R.50. Son profil de jante en carbone de 50mm offre un avantage aérodynamique significatif sans compromettre la stabilité par vent latéral. Légère et rigide, elle assure une précision de pilotage chirurgicale et une accélération explosive, faisant d''elle le choix idéal pour les coursiers et les amateurs de vitesse sur route.', 'Roue avant route carbone profil 50mm.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (209, 209, 44.95, 127, 'Schwalbe G-One Allround', 'Le pneu Schwalbe G-One Allround est la référence absolue pour le Gravel polyvalent. Son profil à petits crampons ronds offre un roulement fluide et rapide sur l''asphalte tout en garantissant une accroche surprenante sur les chemins de terre et le gravier. Doté de la technologie Tubeless Easy et d''un renfort anti-crevaison, c''est le pneu idéal pour l''aventure sans limites.', 'Pneu Gravel polyvalent route et chemins.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (210, 210, 24.95, 128, 'Schwalbe Aerothan Tube', 'Révolutionnez le comportement de votre vélo avec la chambre à air Schwalbe Aerothan. Fabriquée en polyuréthane thermoplastique (TPU), elle est nettement plus légère qu''une chambre classique tout en offrant une résistance supérieure aux crevaisons et aux pincements. Elle procure une sensation de pilotage proche du tubeless et conserve la pression plus longtemps.', 'Chambre à air ultra-légère en TPU haute technologie.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (211, 211, 89.95, 133, 'Natural Fit Nuance Lite', 'La selle Natural Fit Nuance Lite est conçue pour offrir un confort optimal lors des longues journées en selle. Sa forme ergonomique en T permet une grande liberté de mouvement des jambes, tandis que le canal de soulagement central réduit la pression sur les zones sensibles. Avec ses rails en CrMo et son rembourrage léger, elle allie durabilité et performance pour le VTT ou la route.', 'Selle ergonomique avec canal de soulagement périnéal.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (212, 212, 129.95, 113, 'Newmen Advanced 318.0', 'Le cintre Newmen Advanced 318.0 en carbone UD offre le mélange parfait entre rigidité pour la précision de pilotage et flexibilité pour le confort. Sa construction haut de gamme absorbe les vibrations du terrain, réduisant la fatigue des bras et des mains. Avec sa largeur de 740mm et sa géométrie éprouvée, il offre un contrôle total de votre VTT dans les sections techniques.', 'Cintre plat en carbone UD haut de gamme.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (202, 202, 89.95, 150, 'CUBE Helmet MTB PRO', 'Affrontez les sentiers en toute confiance avec le casque CUBE MTB PRO. Sa forme enveloppante descend bas sur l''arrière du crâne pour une protection accrue en tout-terrain. Il dispose d''une visière amovible pour vous protéger du soleil et des branches, ainsi que d''un filet anti-insectes frontal. Son système d''ajustement à une main permet un maintien parfait sur tous les terrains.', 'Casque VTT avec protection arrière étendue.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (213, 213, 99.95, 119, 'ACID Front Wheel MTB/Trekking CMPT 29"', 'Cette roue avant ACID CMPT 29 pouces est le choix idéal pour remplacer une roue ou assembler un vélo VTT/Trekking robuste. Sa jante large de 30mm en aluminium HPA permet de monter des pneus volumineux pour plus de confort et d''adhérence. Assemblée avec des rayons durables et un moyeu fluide à blocage rapide, elle offre un excellent rapport qualité-prix pour une utilisation quotidienne intensive.', 'Roue avant 29" polyvalente VTT et Trekking.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (33, 33, 5995.00, 13, 'ACID Lock FOLDING 80', 'Sécurisez votre vélo avec l''antivol pliant ACID FOLDING 80, offrant un compromis parfait entre sécurité et transportabilité. Ses barres en acier trempé sont reliées par des rivets spéciaux pour résister aux attaques, tout en restant flexibles pour attacher le cadre à divers points fixes. Livré avec un support de cadre pratique, il se fait oublier lors de vos trajets tout en restant toujours à portée de main.', 'Antivol pliant compact en acier trempé.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (35, 35, 1495.00, 15, 'ACID Lock CABLE 120', 'Idéal pour les arrêts rapides, cet antivol à câble spirale ACID offre une longueur généreuse de 120 cm tout en restant très compact une fois enroulé. Son câble en acier tressé est recouvert d''un vinyle protecteur pour préserver votre cadre, et son support de fixation inclus permet de l''avoir toujours sur le vélo. C''est la solution parfaite pour une sécurité de base ou pour sécuriser des accessoires (roues, selle).', 'Câble spirale flexible de 120cm de long.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (49, 49, 2995.00, 16, 'CUBE Frame Lock Pro', 'Le CUBE Frame Lock Pro est un antivol de cadre (type fer à cheval) discret et efficace pour bloquer la roue arrière lors de vos arrêts minute. Il s''intègre parfaitement au design de votre vélo et peut être complété par une chaîne enfichable pour attacher le vélo à un point fixe. C''est la première ligne de défense indispensable pour tout vélo urbain ou trekking.', 'Antivol de cadre fixe pour blocage de roue arrière.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (205, 205, 14.95, 89, 'CUBE Bike Cleaner 1L', 'Prenez soin de votre monture avec le nettoyant vélo CUBE Bike Cleaner. Sa formule puissante mais biodégradable dissout la boue, la graisse et la saleté tenace sans agresser les joints, les roulements ou la peinture délicate. Compatible avec tous les matériaux, y compris le carbone et l''aluminium mat, il laisse un film protecteur qui facilite les lavages futurs.', 'Nettoyant vélo universel puissant et biodégradable.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (206, 206, 9.95, 88, 'ACID Chain Oil Dry 50ml', 'Le lubrifiant chaîne ACID Dry est formulé spécifiquement pour les conditions sèches et poussiéreuses. Il pénètre profondément dans les maillons pour réduire la friction et assurer un passage de vitesses fluide et silencieux. Sa formule sèche ne colle pas, évitant l''accumulation de poussière et de saleté qui accélère l''usure de la transmission.', 'Lubrifiant chaîne haute performance conditions sèches.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (207, 207, 29.95, 85, 'ACID Pump RACE PURE', 'Ne restez jamais en rade avec la mini-pompe ACID RACE PURE. Usinée CNC en aluminium, elle est à la fois ultra-légère et capable d''atteindre des pressions élevées (jusqu''à 100 PSI) pour les vélos de route. Son raccord flexible extensible protège la valve lors du gonflage, et sa tête compatible Presta/Schrader la rend universelle. Un must-have dans votre poche de maillot.', 'Mini-pompe haute pression en aluminium CNC.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (208, 208, 49.95, 91, 'ACID Multi Tool HUSK 24', 'Le multitool ACID HUSK 24 est une véritable caisse à outils format de poche. Ses deux tubes en aluminium abritent 24 fonctions essentielles, incluant clés Allen, Torx, dérive-chaîne, démonte-pneu et même un kit de réparation tubeless. Conçu pour se glisser discrètement dans le cintre (avec support optionnel) ou dans une poche, il vous sauvera de presque toutes les pannes mécaniques.', 'Outil multifonction premium 24-en-1.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (45, 45, 3495.00, 31, 'CUBE Pedals RFR Flat', 'Les pédales plates CUBE RFR offrent une plateforme large et stable pour un contrôle optimal de votre vélo, que ce soit en ville ou sur les sentiers. Fabriquées en thermoplastique renforcé, elles sont légères et extrêmement résistantes aux chocs. Les picots moulés et la surface texturée garantissent une excellente accroche de la chaussure, même par temps humide ou boueux.', 'Pédales plates légères en nylon renforcé.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (34, 34, 3999.00, 14, 'ACID Lock CHAIN 100', 'L''antivol chaîne ACID CHAIN 100 est conçu pour offrir une protection robuste dans les zones à risque modéré. Ses maillons en acier de 6mm sont recouverts d''une gaine textile durable qui protège la peinture de votre vélo contre les rayures et les chocs. Avec sa longueur d''un mètre, il offre une grande flexibilité pour attacher votre vélo à des poteaux larges ou des barrières urbaines.', 'Chaîne en acier cémenté avec maillons de 6mm.
INSERT INTO public.accessoire (id_article, id_reference, prix_article, id_categorie, nom_article, description_article, resumer_article, id_matiere_accessoire, nombre_vente_article, pourcentage_remise, numero_reference) VALUES (48, 48, 4995.00, 9, 'ACID Front Carrier', 'Optimisez votre capacité de chargement avec ce porte-bagages avant ACID HIC RILink. Spécialement conçu pour se fixer sur la fourche, il offre une plateforme stable pour vos sacoches avant ou un petit panier. Sa construction en aluminium assure une durabilité exceptionnelle sans alourdir la direction de votre vélo, parfait pour équilibrer vos bagages lors de longues randonnées.', 'Porte-bagages avant fixation sur fourche.
