INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (19, 4, 8, 2, 1049.00, 69, 'NULANE  - PRO FE', 'Polyvalence et rapidité ne sont pas des notions incompatibles. Le Nulane FE est entièrement équipé d’un garde-boue, d''un porte-bagages arrière, de feux et d''une béquille, et il y a même des supports de fixation pour porte-bagages de type low-ride sur la fourche entièrement en carbone, si vous avez besoin de transporter encore plus d’affaires. Mais tout ce côté pratique ne sacrifie pas les performances. Ses tubes de cadre en aluminium double épaisseur Superlite permettent de conserver sa légèreté. Nous avons ajouté des points de fixation pour disques Flat Mount, par souci d‘élégance et pour une plus grande qualité de freinage. Enfin, nous l’avons également équipé d’axes traversants à l’arrière et à l’avant afin de lui conférer une excellente réactivité qu’il soit chargé ou pas.', 'Plus qu''un simple vélo, le Nulane Pro FE est presque un compagnon de vie. Doté d''une fourche en carbone confortable pour une direction précise et rassurante et de puissants freins à disque hydrauliques pour garantir un arrêt sûr par tous les temps, ses pneus Schwalbe de 40 mm sont à la fois rapides et accrocheurs, que vous rouliez en ville ou sur des routes de campagne. La plage de vitesses Cues 1x9 de Shimano, très étendue et facile à utiliser, vous aidera à affronter n''importe quel itinéraire. Nous avons également inclus une gamme complète d''accessoires pratiques, dont un porte-bagages, des garde-boue et des phares, pour que vous soyez prêt à affronter n''importe quel trajet. Pour le travail, pour les loisirs, pour n''importe quelle raison... il vous soutient.', 612, '2024-07-07', 2);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (12, 18, 6, 1, 1749.00, 63, 'AXIAL WS RACE', 'Nous avons conçu le cadre de l’Axial WS Pro pour une efficacité et un contrôle optimaux. Avec son tube de direction conique, son tube inférieur ovalisé et des axes traversants à l’arrière et à l’avant, il offre une vitesse et une précision de pilotage sans compromis. Ses freins à disque vous permettent d’aborder même les plus longues descentes en toute sécurité. Enfin, sa fourche en carbone et ses haubans plus étroits vous assurent un confort longue durée sur votre selle.', 'Tirer un maximum de profit de votre entraînement, de vos compétitions et de vos sorties est important. C’est pourquoi nous avons créé l’Axial WS Race. Conçu pour combiner confort et vitesse, la fiabilité et la performance légendaires des composants Shimano 105 vous garantissent des sorties ensoleillées. Sa transmission 2x11 vitesses fluide et profonde s’attaque à n’importe quelle route en toute facilité, tandis que ses puissants freins à disque vous confèrent toute la confiance nécessaire pour repousser vos limites, quelle que soit la météo. Roulez vite, roulez librement.', 618, '2025-09-28', 1);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (20, 4, 8, 2, 1249.00, 69, 'NULANE - RACE FE', 'Polyvalence et rapidité ne sont pas des notions incompatibles. Le Nulane FE est entièrement équipé d’un garde-boue, d''un porte-bagages arrière, de feux et d''une béquille, et il y a même des supports de fixation pour porte-bagages de type low-ride sur la fourche entièrement en carbone, si vous avez besoin de transporter encore plus d’affaires. Mais tout ce côté pratique ne sacrifie pas les performances. Ses tubes de cadre en aluminium double épaisseur Superlite permettent de conserver sa légèreté. Nous avons ajouté des points de fixation pour disques Flat Mount, par souci d‘élégance et pour une plus grande qualité de freinage. Enfin, nous l’avons également équipé d’axes traversants à l’arrière et à l’avant afin de lui conférer une excellente réactivité qu’il soit chargé ou pas.', 'Vous avez besoin d''un vélo rapide, confortable, pratique... et résistant. Nous vous avons compris. Vous avez découvert le Nulane Race FE ? Équipé d''un ensemble complet d''accessoires, dont des garde-boue et un porte-bagages, il est prêt à affronter toutes les situations, des trajets quotidiens aux longues randonnées du weekend, sur pratiquement toutes les surfaces. Sa fourche en carbone des plus confortables vous permet d’absorber les irrégularités de la route. Les pneus Schwalbe G-One de 40 mm, adhérents et à roulement rapide, assurent le confort et permettent de garder le contrôle. Les vitesses Cues 2x10 de Shimano mettent le bon rapport pour chaque scénario littéralement à portée de main. Les puissants freins à disque hydrauliques vous permettent de vous arrêter en toute sécurité, quelles que soient les conditions météorologiques. Quelle sera votre prochaine destination ?', 120, '2025-04-07', 3);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (26, 7, 11, 2, 659.00, 69, 'NATURE - ONE', 'Rouler à vélo doit rester un plaisir, c’est pourquoi nous nous sommes tout autorisés pour rendre le cadre du Nature aussi léger que possible sans compromettre sa solidité. Le secret réside dans ses tubes en aluminium à double couches, avec des jonctions bénéficiant de la technique Smooth Welding pour une finition naturellement élégante. Il a été entièrement testé dans nos laboratoires afin que vous soyez assuré qu’il est à la hauteur. Nous avons prévu suffisamment de débattement pour pouvoir l’équiper de pneus pouvant aller jusqu’à 50 mm de large, pour un pilotage aussi confortable que possible. Il est équipé d’un tube de direction conique pour un pilotage de précision, d’un porte-bagages et de points de fixation pour garde-boue élégants pour des équipements futurs. Il dispose de tout ce dont vous avez besoin pour des journées en toute décontraction à la campagne.', 'Le Nature ONE symbolise la liberté sur deux roues, qu’il s’agisse d’une sortie en ville ou d’une exploration plus poussée le weekend. Construit avec les notions de confort et de fiabilité en tête, nous l’avons équipé d’une fourche suspendue Suntour pour que vous puissiez tirer votre épingle du jeu même sur les terrains les plus accidentés. Son système de transmission Essa 1x8 vitesses a la profondeur nécessaire pour vous attaquer en toute facilité à n’importe quel type d’itinéraire. Enfin, ses puissants freins à disque hydrauliques toutes saisons sont la garantie de freinages sécurisés, quelles que soient les conditions. Quelle sera votre prochaine destination ?', 475, '2024-10-24', 2);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (9, 7, 6, 1, 1199.00, 63, 'AXIAL WS PRO', 'Nous avons conçu le cadre de l’Axial WS Pro pour une efficacité et un contrôle optimaux. Avec son tube de direction conique, son tube inférieur ovalisé et des axes traversants à l’arrière et à l’avant, il offre une vitesse et une précision de pilotage sans compromis. Ses freins à disque vous permettent d’aborder même les plus longues descentes en toute sécurité. Enfin, sa fourche en carbone et ses haubans plus étroits vous assurent un confort longue durée sur votre selle.', 'Nous avons conçu l’Axial WS pour combler l’écart entre le vélo de course axé performances et le vélo de randonnée orienté longues distances. Avec son cadre spécialement conçu par nos ingénieurs pour répondre aux exigences de ses puissants freins à disque, quelle que soit la météo, vous disposez d’un contrôle de tous les instants. Sa transmission Shimano Sora 2x9 vitesses met à portée de doigts sa profondeur de braquets, et ses composants de finition des plus confortables, tandis que ses pneus Continental Ultra Sport légers et adhérents, complètent l’ensemble. La seule interrogation est de savoir quelle sera votre prochaine destination?', 862, '2024-08-08', 3);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (2, 4, 9, 1, 5699.00, 58, 'PHENIX C:68X - SLX', 'Nous connaissons quelques petites choses matière de construction de cadres de cross-country de compétition rapides, mais le nouveau Phenix C:68X met la barre encore plus haut. Comment ? Tout d’abord, nous avons utilisé notre carbone C:68X® de première qualité pour une légèreté, une robustesse et un confort ultimes. Nous avons redessiné la jonction de tube de selle pour incorporer un collier de selle intégré. Il s’agit là d’une petite touche, mais avec le passage interne de câbles, cela contribue à l’élégance du cadre et à ses lignes organiques. Il dispose également d’un jeu de direction à angle ajustable afin que vous puissiez peaufiner le pilotage de votre vélo pour mieux répondre à vos préférences.', 'Se déplacer le plus rapidement possible d’un point A à un point B en cross-country requiert des composants qui ne vous laisseront pas tomber. C’est pourquoi, pour l’Phenix C:68X SLX, nous sommes partis d’un groupe XO Eagle Transmission tout électrique afin que vous disposiez d’une énorme profondeur de braquets à portée de doigts. Sa fourche RockShox SID Ultimate, ses roues Newmen Advanced SL X.A.30 très légères, ses pneus Schwalbe de 2,25 pouces très adhérents et ses chambres à air Schwalbe Aerothan ultra légères permettent au caoutchouc de rester en parfait contact avec le sol pour un maximum de vitesse et de contrôle. Son combo cintre/potence est léger et confortable. Grâce à ses freins Magura MT8 Pro, tu peux rouler plus vite et plus longtemps.', 307, '2024-07-18', 1);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (10, 14, 5, 2, 1299.00, 63, 'ATTAIN - RACE', 'Nous avons construit le châssis en aluminium semi-aérodynamique 6061 de l''Attain en gardant à l''esprit à la fois le confort et les performances. Un tube diagonal à profil en forme de larme aide le cadre à fendre l''air et le passage de câbles intégré réduit l''encombrement, tandis que Smooth Welding donne aux joints de tube un aspect haut de gamme, presque organique. Son tube de direction conique et ses axes traversants à l’avant et l’arrière lui garantissent une parfaite précision et un transfert de puissance des plus efficaces. Ses haubans plus étroits et sa fourche tout en carbone permettent de réduire les vibrations et d’absorber les irrégularités de la route pour un plus grand confort du cycliste, et il y a de la place pour des pneus jusqu''à 32 mm. Et, avec un collier de serrage de tige de selle parfaitement intégré et des points de montage sous le tube supérieur, nous aimons à penser que nous avons pensé à tout... pour que vous ayez à faire soit rouler.', 'L’idée derrière l’Attain Race est qu’il combine la vitesse d’un vélo de route semi-aéro au confort d’un vélo de randonnée ultra léger. Ainsi, par exemple, nous avons monté une transmission Tiagra 2x10 de Shimano et des freins à disque hydrauliques Tiagra correspondants. Pneus Continental Grand Prix légers et très adhérents, dans une largeur confortable de 30 mm. Le tout est complété par une fourche en carbone légère et un cadre conçu à la fois pour le confort et la vitesse, afin que vous puissiez élargir vos horizons dans la direction qui vous convient.', 938, '2025-03-16', 3);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (3, 9, 23, 1, 6399.00, 59, 'STEREO ONE22 C:62 - SLT 29', 'Quand on tient déjà quelque chose de génial, l''astuce consiste à le peaufiner ; et c''est exactement ce que nous avons fait ici. Le cadre Stereo ONE22 C:62 reprend notre célèbre concept de suspension à débattement moyen et le réinvente avec notre construction en carbone Advanced Twin Mold C:62®. Un jeu de direction à angle réglable vous permet d''ajuster l’Agile Trail Geometry en fonction de votre style de conduite. Une paire de languettes ISCG facilite l''installation d''un dispositif de chaîne, tandis que le passage de câbles interne complète l''apparence organique du cadre. Et grâce à l''interface UDH, vous pouvez vous attendre à un changement de vitesse durable et ultra fiable. Il est paré à tout type de scénario.', 'Léger, vif, polyvalent et doté d''un confort accru, voilà en résumé ce qu’est le Stereo ONE22 C:62 SLT. Nous avons commencé par la transmission XX Eagle de Sram, car elle offre une vaste gamme de vitesses avec une précision électronique tout en établissant une nouvelle norme en matière de robustesse et de fiabilité. Vous ne reviendrez pas de la puissance des freins à disque hydrauliques à quatre pistons Sram Level Ultimate assortis car, avec une fourche Fox 34 Float Factory GripX de 130 mm à l''avant et l’amortisseur Float Factory correspondant à l''arrière, vous négocierez les virages avec une rapidité inédite. Les roues légères Newmen Phase 30 et les pneus Schwalbe Nobby Nic et Wicked Will de 2,4 pouces avec chambres à air Aerothan aident à maintenir le contact avec le sol à tout moment et un combo barre/potence en carbone monobloc vous donne un contrôle total.', 437, '2024-10-19', 4);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (5, 11, 2, 1, 649.00, 57, 'AIM - PRO', 'La beauté ne se limite pas à l’aspect extérieur : l''élégance de l''Aim est due à ses jonctions de tube de direction et de tube de selle aux soudures lisses, flattées par un passage interne des câbles pour réduire l''encombrement. Vous trouverez donc un boîtier de pédalier fileté et un support de fixation pour disque de type Flat Mount intégrant naturellement notre support de fixation pour béquille, ainsi que des points de fixation intégrés pour porte-bagages et garde-boue ACID. Sa géométrie soigneusement pensée comprend un tube inférieur plus bas pour une plus grande confiance, et il dispose également de suffisamment de débattement pour l’équiper de pneus de 2,25 pouces larges, adhérents et confortables. Notre système SizeSplit a permis d’étendre également la gamme, ainsi, quelle que soit votre taille, vous trouverez forcément un Aim qui vous convient parfaitement.', 'Vous êtes atteint de la fièvre du tout-terrain ? L’Aim Pro pourrait vous apporter le soulagement que vous cherchez. Avec ses pneus 2,25 pouces adhérents et sa fourche suspendue de 100 mm, il dispose du confort et du contrôle dont vous aurez besoin pour continuer de pédaler jusqu’à la fin de votre itinéraire. La transmission Shimano Cues à 10 vitesses à grande plage de rapports vous aidera à atteindre le sommet des pentes les plus abruptes, tandis que ses puissants freins à disque hydrauliques vous garantissent un contrôle de tous les instants en descente. Cerise sur le gâteau, le système SizeSplit permet à chaque cycliste de trouver facilement un modèle parfaitement adapté, tandis que son cadre peut également être équipé d’une tige de selle télescopique.', 371, '2024-08-10', 3);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (6, 2, 1, 1, 8299.00, 65, 'AERIUM C:68X - SLX', 'Développé en étroite collaboration avec l''athlète CUBE et championne du monde Lucy Charles-Barclay, l''Aerium C:68X peut légitimement revendiquer son titre de cadre le plus rapide que nous ayons jamais fabriqué. Chaque détail, de la conception de la fourche aérodynamique à l''intégration des freins à disque et aux systèmes d''hydratation et de stockage dans le cadre, a été conçu avec zéro compromis, testé dans la soufflerie puis de nouveau testé sous pression, en conditions de course. Lucy a une confiance totale dans le cadre ainsi que dans son stockage et son hydratation configurables, et maintenant vous aussi pouvez avoir une totale confiance.', 'Vous savez que ce vélo est rapide, car l''Aerium C:68X SLX est étroitement inspiré du vélo de la triathlète Lucy Charles-Barclay, qui a remporté le championnat du monde. Des options d''hydratation et de stockage configurables se trouvent derrière les capots aérodynamiques, avec la possibilité de faire fonctionner jusqu''à deux systèmes d''hydratation pour une meilleure auto-suffisance, même pendant les longues courses. Nous avons monté un jeu de roues Newmen STREEM Aero/Sprint TT 60/80 Vonoa avec des pneus Continental Grand Prix 5000 TT et des valves aérodynamiques pour une accélération rapide, des performances aérodynamiques supérieures et une adhérence sûre dans les virages. Le système de cockpit CUBE C:68X® place le cycliste dans la position optimale, avec une large gamme de réglages pour les barres aérodynamiques. Un système d''entraînement Sram Force AXS 2x12 avec puissance mètre intégré garantit que le bon engrenage est toujours là. Et absolument tout est fabriqué à partir de notre carbone C:68X® premium. Nous avons travaillé les moindres détails pour que votre sueur soit convertie en vitesse pure.', 333, '2024-08-04', 4);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (8, 13, 6, 2, 1049.00, 63, 'AXIAL WS', 'Un vélo rapide devrait également être confortable, et cadre en aluminium double couche et compatible avec des freins à disque de l’Axial WS combine ces deux qualités dans un package merveilleusement intégré. Son tube de direction conique et son tube diagonal ovalisé vous garantissent un pilotage précis et un mouvement vers l’avant des plus rapides, tandis que sa fourche en carbone et ses haubans plus minces permettent d’absorber les irrégularités de la route afin de vous garantir un confort optimal. En tout objectivité, vous avez là le meilleur des deux univers.', 'Le cocktail de vitesse et de confort que représente l’Axial WS est une introduction parfaite au monde de la liberté et de l’activité physique sur deux roues. Son système de freins à disque est la garantie de pouvoir ralentir et vous arrêter en toute sécurité, quelle que soit la météo. Sa transmission Shimano Claris 2x8 offre profondeur, facilité d’usage et fiabilité exceptionnelle. Il est équipé d’une fourche en carbone pour un surplus de confort de pilotage, et nous avons soigneusement sélectionné les points de contacts que sont la selle et le cintre afin de vous garantir le même confort du débout à la fin de votre sortie. Ajoutez à cela une paire de pneus Continental Ultra Sport adhérents et très roulants, et vous êtes paré.', 524, '2024-08-25', 4);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (11, 1, 5, 2, 1499.00, 63, 'ATTAIN - SLX', 'Nous avons construit le châssis en aluminium semi-aérodynamique 6061 de l''Attain en gardant à l''esprit à la fois le confort et les performances. Un tube diagonal à profil en forme de larme aide le cadre à fendre l''air et le passage de câbles intégré réduit l''encombrement, tandis que Smooth Welding donne aux joints de tube un aspect haut de gamme, presque organique. Son tube de direction conique et ses axes traversants à l’avant et l’arrière lui garantissent une parfaite précision et un transfert de puissance des plus efficaces. Ses haubans plus étroits et sa fourche tout en carbone permettent de réduire les vibrations et d’absorber les irrégularités de la route pour un plus grand confort du cycliste, et il y a de la place pour des pneus jusqu''à 32 mm. Et, avec un collier de serrage de tige de selle parfaitement intégré et des points de montage sous le tube supérieur, nous aimons à penser que nous avons pensé à tout... pour que vous ayez à faire soit rouler.', 'Vous pouvez rouler plus vite si vous pouvez également vous arrêter plus vite. C''est la raison pour laquelle, pour l''Attain SLX, nous avons choisi les freins à disque hydrauliques Shimano 105 éprouvés et les avons associés à l''accélération rapide et à l''excellente adhérence des pneus Continental Grand Prix de 30 mm sur des roues Newmen Performance R.32, afin que vous puissiez freiner plus tard et garder le contrôle total. Combiné à la fiabilité exceptionnelle de l’emblématique transmission 105 2x12 vitesses de Shimano, à la cadence d''un cadre semi-aérodynamique et au confort d''une fourche entièrement en carbone, vous avez un vélo aussi adapté à une course rapide en club qu''à une longue sortie de weekend.', 360, '2024-08-04', 2);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (27, 15, 11, 2, 769.00, 69, 'NATURE - ONE ALLROAD', 'Le cœur d’un vélo digne de ce nom réside dans la qualité de son cadre. Pour le Nature Allroad, nous sommes partis de tubes en aluminium à double couches (qui permettent de réaliser une économie de poids sans affecter la robustesse du cadre) et avons eu recours à la technique Smooth Welding pour la jonction des tubes principaux pour une finition des plus élégantes. Le résultat est un cadre à la fois léger, réactif et confortable ayant passé haut la main les tests rigoureux auxquels il a été soumis dans nos laboratoires. Il dispose de suffisamment de débattement pour des pneus larges, et nous l’avons équipé de toute une série d’accessoires (comprenant un système d’éclairage, des garde-boue, un porte-bagages et une béquille) afin que vous soyez paré pour tout type de trajet.', 'Combinant le confort et la polyvalence de notre gamme Nature à la commodité de tout un complément d’accessoires, le Nature ONE Allroad est un vélo capable de tout entreprendre. Avec ses garde-boue intégraux, son porte-bagages très robuste, sa béquille et son système d’éclairage alimenté par dynamo vous êtes paré à tout type de scénario, à la ville comme à la campagne. Et sa fourche suspendue Suntour confortable, sa transmission Shimano Essa 1x8 vitesses faciles à utiliser et ses puissants freins à disque hydrauliques vous permettent de vous rendre à votre destination avec confort et sécurité. Maintenant, où vous emmènera votre prochaine sortie?', 908, '2024-10-25', 4);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (14, 5, 5, 1, 2599.00, 63, 'ATTAIN C:62 - SLX', 'Performance et confort sont les deux faces d’une même pièce. Le cadre élégant et semi-aérodynamique de l''Attain C:62 est fabriqué à partir de notre carbone C:62© de qualité supérieure pour vous aider à fendre les airs. Il offre également un transfert de puissance efficace, grâce à ses axes traversants X12 et au profilage soigné du cadre, et le passage de câbles intégré réduit l''encombrement et offre un autre avantage aérodynamique. Tout a donc été pensé pour les performances. Côté confort, les Flex Stays et la fourche entièrement en carbone sont conçus pour réduire la fatigue des cyclistes, même les plus grands en taille ou roulant sur des surfaces routières plus accidentées, et le dégagement pour les pneus jusqu''à 34 mm se traduit par une grande adhérence et un amorti supplémentaire, si nécessaire.', 'Accumuler les kilomètres devrait être un plaisir, et non une corvée. C''est pourquoi nous avons commencé par équiper l''Attain C:62 SLX de la légendaire transmission Shimano 105 Di2 2x12 vitesses et des freins à disque hydrauliques assortis, car ils placent le contrôle de votre vitesse littéralement à portée de main, quelles que soient la route ou les conditions météorologiques. Un Newmen Advanced Carbon Wing Bar complète le cadre semi-aéro et la fourche entièrement en carbone pour économiser des watts. Les roues Newmen Evolution SL R.35 légères et à accélération rapide sont chaussées de pneus Continental Grand Prix de 30 mm et de tubes Aerothan ultra-légers, pour la meilleure association polyvalente d''adhérence tenace et de confort tout au long de la journée. Le reste ? Et bien, ça dépend de vous.', 642, '2024-10-12', 4);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (16, 1, 8, 2, 769.00, 69, 'NULANE - ONE', 'Le mélange de praticité, de robustesse et de rapidité du Nulane est intégré dans chaque caractéristique de son cadre en aluminium à double paroi. Avec des roues à dégagement rapide pour faciliter le transport ou le stockage, il est également équipé de garde-boue et de points de fixation de porte-bagages pour une mise à niveau facile vers des fonctions de tourisme ou de navette. Les supports de disques sont de série, bien sûr, et la fourche entièrement en carbone ajoute au confort et à la précision de la direction.', 'Le Nulane ONE reprend l''ADN de nos vélos de route inspiré par la course, y ajoute une certaine praticité de vélo de tourisme et une dose de robustesse du flat bar. Le résultat? Un vélo unique, capable d''aller partout et de tout faire. Ses puissants freins à disque hydrauliques Shimano offrent une puissance de freinage à la fois progressive et rassurante, quelle que soit la météo. La transmission simple Essa 1x8 vitesses de Shimano vous permettra d’atteindre votre destination et d’en revenir. Enfin, le confort et la légèreté de sa fourche en carbone vous permettent d’aborder même les routes les plus accidentées. Il s''agit d''un ensemble complet, quelle que soit votre destination.', 638, '2024-12-16', 1);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (17, 5, 8, 2, 849.00, 69, 'NULANE - PRO ', 'La version trapèze de notre polyvalente Nulane a l''air familière, et il y a une raison à cela - elle est confortable, pratique et facile à monter et à démonter. Mais ce n''est pas toute l''histoire, car les détails sont aussi importants. Des tubes de cadre Superlite en aluminium à double rainure et une fourche entièrement en carbone aident à maintenir le poids à un niveau bas. Il y a des points de montage pour un simple garde-boue et des mises à niveau des porte-bagages avant et arrière. Et les essieux thru à l''avant et à l''arrière sont une touche technique que vous apprécierez, car ils permettent de conserver une maniabilité sûre, sécurisée et prévisible, que vous portiez une charge ou non.', 'Le Nulane Pro s''inspire de plusieurs de nos séries de vélos. La robustesse du gravel, la cadence de la route et la praticité du tourisme se combinent pour former un vélo facile à vivre et d''une polyvalence presque infinie. Grâce à la large plage de vitesses Shimano Cues 1x9, il suffit d''un ou deux clics pour trouver le bon rapport pour n''importe quel scénario. Les pneus Schwalbe de 40 mm à roulement rapide s''adaptent aussi bien aux rues urbaines qu''aux routes de campagne, et les puissants freins à disque hydrauliques fonctionnent dans toutes les conditions météorologiques. Avec une fourche confortable en carbone qui vous maintient dans la bonne direction, c''est tout ce dont vous avez besoin pour le travail ou les loisirs.', 407, '2024-11-06', 2);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (18, 15, 8, 2, 1549.00, 69, 'NULANE - RACE', 'La forme rencontre la fonction et la perfection dans le cadre du Nuroad. Fabriqué en aluminium haute performance avec un grand souci du détail, il allie l''esthétique organique d''un vélo de course à la fonctionnalité pratique d''un vélo de tourisme. Le passage interne avancé des câbles, le collier de selle intégré et la soudure lisse contribuent tous à son aspect svelte, tandis que la fourche rigide en carbone ajoute une dose de confort réduisant les vibrations. Mais il est aussi très pratique, avec un dégagement pour des pneus allant jusqu''à 50 mm (45 mm avec les garde-boue), une compatibilité avec notre porte-bagages arrière SIC 2.0 et de nombreux points de fixation pour les accessoires.', 'Sous les lignes épurées et organiques du cadre et de la fourche en carbone du Nuroad Race se cache un vélo aux intentions sérieuses. Les vitesses Shimano GRX 2x12 offrent une vaste gamme de rapports pour dévaler les collines avec la durabilité fiable d''un équipement construit spécifiquement pour l''utilisation sur le gravier. Les freins à disques hydrauliques et les pneus Schwalbe G-One R 45 mm montés sur les roues Newmen Performance Gravel 25 offrent une excellente adhérence et un bon contrôle par tous les temps. De plus, grâce à une vaste gamme de points de fixation sur le cadre et la fourche pour des accessoires tels que les garde-boue et les porte-bagages, il est aussi adaptable que robuste.', 11, '2025-02-06', 1);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (28, 12, 15, 2, 749.00, 71, 'TOURING - ONE', 'L’âme du Touring est son cadre aluminium à double épaisseur ultra léger de très haute qualité. L’option Trapeze est simple et élégante, sans sacrifice de robustesse ou de sécurité. Conçu à l’aide de notre technologie Efficient Comfort Geometry afin de vous garantir un maximum de confort lors des longues journées passées sur la selle, son agilité et son pilotage intuitif ne manqueront pas vous surprendre. Grâce à son passage interne de câbles dans le tube inférieur pour des changements de vitesse fiable, précis et nécessitant peu d’entretien, vous n’avez plus qu’à vous concentrer sur votre plaisir de pédaler. Notre élégant porte-bagages semi-intégré vous permet d’emporter tout ce dont vous avez besoin pour le trajet, et la béquille Flat Mount vous permet de stationner votre vélo en un clin d''œil.', 'Le Touring ONE est parfaitement équipé et bénéficie d’un design confortable vous permettant d’élargir vos horizons cyclistes. La facilité d’usage de sa transmission Shimano Essa vous permet d’aborder en toute facilité n’importe quel type de route en mettant à portée de vos doigts sa sélection de braquets. Ses puissants freins à disque fonctionnent mieux que n’importe quel autre système de freinage classique et vous garantissent un freinage sécurisé sur les routes humides. Sa fourche suspendue et ses pneus de 47 mm de large offrent une meilleure absorption des routes accidentées. Enfin, grâce ses garde-boue intégraux, son porte-bagages élégamment intégré, son système d’éclairage par dynamo, intégré également, et sa béquille des plus pratiques, il possède tout ce dont vous avez besoin pour vos petits déplacements en ville et vos longues sorties en campagne.', 728, '2025-05-19', 3);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (29, 16, 13, 2, 799.00, 71, 'HYDE - ONE', 'Son aspect est familier, mais ne vous y trompez pas. Tirant son inspiration de nos VTT hardtails de 29 pouces, le cadre trapèze du Hyde est équipé de tubes double épaisseur pour vous offrir une structure à la fois résistante, légère et dynamique. Élégamment intégré, son porte-bagages et ses supports de fixation pour garde-boue facilitent la personnalisation, et il dispose de suffisamment d’espace pour l’équiper d’un porte-bidon pour les longues sorties du week-end. Sa fourche en carbone et légère complète parfaitement l’ensemble, offrant un pilotage précis et réactif tout à fait adapté à sa légèreté et sa sobriété apparente.', 'Quoi de plus approprié que le deux roues pour prendre le meilleur sur la circulation? C’est cette réflexion qui est à l’origine du Hyde ONE, notre modèle polyvalent urbain aussi simple que robuste et inspiré de nos VTT 29 pouces. Ses pneus Schwalbe Big Apple très roulants sont parfaitement adaptés à un usage urbain, tandis que ses puissants freins à disque hydrauliques Shimano offrent une impressionnante puissance de freinage, quelles que soient les conditions météorologiques. Sa fourche en carbone vous permet de rester bien ancré au sol et son pilotage est incroyablement précis. Enfin, sa transmission Shimano Essa 8 vitesses facile à utiliser vous offre la profondeur nécessaire pour vous déplacer en toute aisance en ville.', 310, '2025-02-28', 2);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (30, 5, 14, 2, 849.00, 69, 'KATHMANDU - ONE', 'L’attention au détail est une obsession chez CUBE. C’est pourquoi nous avons conçu le nouvel Integrated Carrier 3.0 du Kathmandu pour fonctionner en parfaite harmonie avec notre garde-boue ACID. Nous l’avons équipé d’un frein à disque Flat Mount à l’arrière (également compatible avec une béquille) et avons eu recours à notre technologie Smooth Welding pour offrir au cadre des jonctions nettes et une apparence organique. Mais ce n''est pas seulement une question d''élégance. Nous avons équipé la base arrière d’un axe traversant et ajouté un tube de direction conique pour une plus grande rigidité et une plus grande précision de pilotage, même chargé au maximum. Avec le passage interne des câbles pour compléter l''aspect épuré du cadre, il nous semble que nous avons pensé à tout. Pensez simplement à emporter votre brosse à dents.', 'Le vélo, c''est le voyage. La simplicité discrète du Kathmandu ONE trahit sa robustesse et son adaptabilité. Regardez de plus près et vous remarquerez le porte-bagages intégré robuste et polyvalent 3.0 : la base idéale pour transporter tout ce dont vous avez besoin. Des garde-boue ACID sur toute la longueur, des feux avant et arrière et une béquille plate sont inclus. Les vitesses 2x9 Cues de Shimano, faciles à utiliser, mettent le bon rapport à votre portée, et les freins à disque hydrauliques Shimano vous permettent de vous arrêter en toute sécurité, quel que soit le temps. La fourche suspendue Suntour permet d''adoucir les routes les plus accidentées, et les pneus Schwalbe Green Marathon utilisent du caoutchouc recyclé dans leur fabrication pour réduire leur impact sur l''environnement. Quelle sera votre prochaine destination ?', 155, '2024-12-18', 4);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (21, 16, 10, 1, 969.00, 68, 'NUROAD - ONE', 'Pour créer le Nuroad, nous nous sommes inspirés de nos vélos de course et de cyclocross, puis nous avons ajouté au mélange un peu d''ADN de VTT. Il y a suffisamment d''espace pour des pneus de 45 mm, larges, maniables et confortables - ou de 40 mm si vous roulez dans la boue en même temps. Il y a un collier de selle intégré, des montages de porte-bagage, de garde-boue et de béquille exceptionnellement propres, et un routage interne des câbles soigné pour garder les transmissions de vitesse à l''abri des gravillons et de la boue pendant plus longtemps. Les roues Thru-axle améliorent la précision de la direction, même sur les routes les plus accidentées. La fourche entièrement en carbone n''est pas seulement légère - elle contribue également à réduire les vibrations provenant de la route et comporte des fixations de porte-bagages lowrider. Et, avec une gamme de tailles étendue allant de XS à XL, nous sommes convaincus que quasiment tous les cyclistes peuvent trouver leNuroad qui leur convient.', 'Routes accidentées ? Aucun problème. La toute nouvelle fourche toute en carbone du Nuroad ONE est confortable et légère, et présente des supports de fixation de type low-rider pour porte-bagages. Ses puissants freins à disque vous confèrent toute la puissance de freinage et la sécurité dont vous avez besoin, quelle que soit la météo. La transmission 1x9, souple et profonde, vous permet de trouver le bon rapport de vitesse pour chaque trajet. Ses pneus Schwalbe 40mm confortables, résistants et très roulants offrent une incroyable adhérence. Enfin, avec son cadre conçu pour répondre à vos besoins, le Nuroad répondra toujours présent, que ce soit pour vos trajets quotidiens ou vos excursions.', 660, '2024-11-19', 2);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (22, 17, 10, 1, 1099.00, 68, 'NUROAD - ONE FE', 'Pour créer le Nuroad FE, nous nous sommes inspirés de nos vélos de course et de cyclocross, puis nous avons ajouté au mélange un peu d''ADN de VTT. Il y a suffisamment d''espace pour des gros pneus de 40 mm de large, adhérents et confortables, même avec les garde-boues pleine longueur inclus. Il y a un porte-bagages arrière exceptionnellement propre, et nous avons inclus une béquille et un système d''éclairage complet. Le routage interne soigné des câbles permet de garder les transmissions à l''abri des gravillons et de la boue pendant plus longtemps. Les roues à démontage rapide sont faciles à monter et à démonter pour le transport ou le stockage. La fourche entièrement en carbone n''est pas seulement légère - elle contribue également à réduire les vibrations provenant de la route et comporte des fixations de porte-bagages lowrider. Et avec des tailles allant de XS à XL, il est facile de trouver le montage parfait.', 'Chez CUBE, nous pensons que le vélo est l''un des outils les plus polyvalents jamais inventés ; et le Nuroad ONE FE pousse cette idée à sa conclusion logique. Entièrement équipé de garde-boue sur toute la longueur, d''un solide porte-bagages, d''une béquille pratique et d''un système d''éclairage qui vous permet de voir et d''être vu, il est aussi à l''aise pour les trajets quotidiens que pour les randonnées en montagne de plusieurs jours. Les puissants freins à disque vous donnent le contrôle dont vous avez besoin par tous les temps, les vitesses 1x9 mettent le bon rapport au bout de vos doigts et la fourche entièrement en carbone, qui comporte des points de fixation pour un porte-bagages supplémentaire, est légère et confortable. Il s''agit d''un paquet complet.', 929, '2025-09-25', 3);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (23, 10, 10, 1, 1399.00, 68, 'NUROAD - PRO', 'Pour créer le Nuroad, nous nous sommes inspirés de nos vélos de course et de cyclocross, puis nous avons ajouté au mélange un peu d''ADN de VTT. Il y a suffisamment d''espace pour des pneus de 45 mm, larges, maniables et confortables - ou de 40 mm si vous roulez dans la boue en même temps. Il y a un collier de selle intégré, des montages de porte-bagage, de garde-boue et de béquille exceptionnellement propres, et un routage interne des câbles soigné pour garder les transmissions de vitesse à l''abri des gravillons et de la boue pendant plus longtemps. Les roues Thru-axle améliorent la précision de la direction, même sur les routes les plus accidentées. La fourche entièrement en carbone n''est pas seulement légère - elle contribue également à réduire les vibrations provenant de la route et comporte des fixations de porte-bagages lowrider. Et, avec une gamme de tailles étendue allant de XXS à XL, nous sommes convaincus que quasiment tous les cyclistes peuvent trouver leNuroad qui leur convient.', 'Le Nuroad Pro va vous poser une question : quelle est votre prochaine destination ? Avec son imposant débattement pour des pneus Schwalbe larges et adhérents, il a potentiel pour aller là où les autres vélos ne se sont jamais aventurés. La transmission Cues 1x11 de Shimano à changement de vitesse rapide met à votre portée le bon rapport pour n''importe quelle route. Ses puissants freins à disque hydrauliques Shimano vous garantissent une capacité de freinage tout à fait exceptionnelle. Enfin, sa fourche en carbone, des plus confortables, lui permet de réduire les vibrations afin que vous puissiez rouler plus longtemps en bénéficiant d’un maximum de confort. Avec son garde-boue, son porte-bagages et sa béquille, vous êtes prêt à l’exploration sur deux roues... où que vous alliez.', 65, '2024-10-18', 2);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (24, 2, 10, 1, 1499.00, 68, 'NUROAD - PRO FE', 'Pour créer le Nuroad FE, nous nous sommes inspirés de nos modèles de cyclo-cross et de course, et avons ajouté au mélange un peu d’ADN de VTT. Il présente un débattement suffisant pour de larges pneus de 40mm adhérents et confortables même si votre vélo est équipé de garde-boue intégraux. Il présente un porte-bagages arrière incroyablement élégant, et nous l’avons équipé d’une béquille et d’un système d’éclairage à l’arrière et à l’avant. Grâce au passage interne des câbles, ceux-ci ne vous gênent pas, tandis que les roues à axe traversant ajoutent une précision de direction supplémentaire pour plus de sécurité et de confiance, même sur les routes les plus accidentées. Sa fourche tout en carbone est non seulement légère, mais permet également de réduire les vibrations de la route, et présente des supports de fixation pour porte-bagages de type lowrider. Et avec des tailles allant de XS à XL, il est facile de trouver le modèle parfait.', 'Pour le Nuroad Pro FE, nous avons conservé les atouts reconnus de notre Nuroad Pro (stabilité de pilotage, design robuste, polyvalence tout-terrain) et l''avons équipé de toute une série d''accessoires pour le transformer rapidement en vélo de randonnée ou à usage quotidien. Il possède donc un porte-bagages très solide, des garde-boue intégraux et un système d’éclairage. Les pneus Schwalbe G-One Comp de 40 mm et la fourche entièrement en carbone facilitent la tâche sur les routes accidentées, la transmission Cues 1x11 vitesses profonde et fluide de Shimano vous maintiennent en mouvement et les robustes freins à disque hydrauliques vous offrent une puissance de freinage par tous les temps. C''est l’ensemble complet pour partir en exploration ou faire la navette.', 610, '2025-02-24', 1);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (25, 6, 10, 1, 1499.00, 68, 'NUROAD - EX', 'Pour créer le Nuroad, nous nous sommes inspirés de nos vélos de course et de cyclocross, puis nous avons ajouté au mélange un peu d''ADN de VTT. Il y a suffisamment d''espace pour des pneus de 45 mm, larges, maniables et confortables - ou de 40 mm si vous roulez dans la boue en même temps. Il y a un collier de selle intégré, des montages de porte-bagage, de garde-boue et de béquille exceptionnellement propres, et un routage interne des câbles soigné pour garder les transmissions de vitesse à l''abri des gravillons et de la boue pendant plus longtemps. Les roues Thru-axle améliorent la précision de la direction, même sur les routes les plus accidentées. La fourche entièrement en carbone n''est pas seulement légère - elle contribue également à réduire les vibrations provenant de la route et comporte des fixations de porte-bagages lowrider. Et, avec une gamme de tailles étendue allant de XXS à XL, nous sommes convaincus que quasiment tous les cyclistes peuvent trouver leNuroad qui leur convient.', 'Lorsque vous partez en exploration, il est important d''avoir le bon matériel à portée de main. C''est pourquoi, pour le Nuroad EX, nous avons choisi les vitesses GRX 1x12 spécifiques au gravel de Shimano : elles sont fiables et offrent une large gamme de vitesses. Une paire de roues Newmen Performance Gravel 25 équipée de pneus Schwalbe 45 mm légers, robustes et adhérents, et de freins à disque hydrauliques Shimano GRX, vous permet de garder le contrôle, en permanence. Sa fourche tout en carbone filtre les vibrations pour un confort garanti toute la journée. Enfin, ses supports de fixation pour garde-boue (avant et arrière) vous donnent la possibilité d’équiper votre Nuroad EX pour n’importe quel type de sortie, qu’il agisse d’un déplacement quotidien ou de sorties plus longues.', 671, '2025-03-27', 1);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (1, 8, 2, 1, 599.00, 57, 'AIM - ONE', 'La beauté ne se limite pas à l’extérieur, l’élégance du tout nouvel Aim repose essentiellement sur des soudures incroyablement lisses au niveau des jonctions du tube de direction et du tube de selle. Il dispose d’un boîtier de pédalier fileté et d’un passage interne de câbles pour des contraintes d’entretien réduites et simplifiées. Il est même équipé d’un support de fixation pour disque Flat Mount qui s’intègre parfaitement avec le support de fixation pour béquille, et les points de fixation intégrés de vos garde-boue et porte-bagages ACID. Sa géométrie soigneusement pensée comprend un tube inférieur plus bas pour une plus grande confiance, et il dispose également de suffisamment de débattement pour l’équiper de pneus de 2,25 pouces larges, adhérents et confortables. Notre système Size Split a permis d’étendre également la gamme, ainsi, quelle que soit votre taille, vous trouverez forcément un Aim qui vous convient parfaitement.', 'Chez CUBE, nous pensons qu’un vélo doit être le plus discret possible et vous laisser pleinement apprécier votre sortie. C''est pourquoi, avec l''Aim ONE polyvalent, nous avons commencé avec une fourche suspendue de 100 mm et des pneus larges et adhérents, afin que vous bénéficiez d’un contrôle et d’un confort de tous les instants. Sa transmission Shimano 2x8 vitesses vous offre toute la profondeur nécessaire d’une simple pression de bouton, tandis que ses puissants freins à disque vous permettent de vous arrêter en toute sécurité, quelle que soit la météo. Enfin, grâce à notre système Size Split et aux nombreuses tailles disponibles, chaque cycliste est en mesure de trouver l’Aim qui lui convient.', 943, '2025-08-13', 3);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (4, 6, 9, 1, 7399.00, 58, 'PHENIX C:68X - SLT', 'Nous connaissons quelques petites choses matière de construction de cadres de cross-country de compétition rapides, mais le nouveau Phenix C:68X met la barre encore plus haut. Comment ? Tout d’abord, nous avons utilisé notre carbone C:68X® de première qualité pour une légèreté, une robustesse et un confort ultimes. Nous avons redessiné la jonction de tube de selle pour incorporer un collier de selle intégré. Il s’agit là d’une petite touche, mais avec le passage interne de câbles, cela contribue à l’élégance du cadre et à ses lignes organiques. Il dispose également d’un jeu de direction à angle ajustable afin que vous puissiez peaufiner le pilotage de votre vélo pour mieux répondre à vos préférences.', 'La puissance et le contrôle sont les clés d’une vitesse débridée. Grâce au combo cintre/potence en une seule pièce du Phenix C:68X SLT offrant confort et légèreté, vous avez tout ce dont vous avez besoin à portée de doigts. Le nouveau groupe XX SL Eagle Transmission de Sram offre des changements de vitesse électroniques ultra précis et rapides ainsi qu''une incroyable profondeur. Ses pneus Schwalbe de 2,25 pouces très adhérents, ses tubes Schwalbe Aerothan ultra légers et ses roues Newmen Phase 30 permettent de tirer profit de toute votre puissance sur les chemins, tandis que sa fourche Fox Float 32 Factory SC avec arc inversé vous garantit stabilité et précision de pilotage. Enfin, pour nous assurer que vous soyez en mesure de maintenir cette vitesse dans les moindres virages, nous l’avons équipé de puissants freins Magura MT8 SL avec disque à l’avant.', 962, '2025-06-20', 4);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (7, 12, 5, 2, 999.00, 63, 'ATTAIN - PRO', 'Nous avons construit le châssis en aluminium semi-aérodynamique 6061 de l''Attain en gardant à l''esprit à la fois le confort et les performances. Un tube diagonal à profil en forme de larme aide le cadre à fendre l''air et le passage de câbles intégré réduit l''encombrement, tandis que Smooth Welding donne aux joints de tube un aspect haut de gamme, presque organique. Son tube de direction conique et ses axes traversants à l’avant et l’arrière lui garantissent une parfaite précision et un transfert de puissance des plus efficaces. Ses haubans plus étroits et sa fourche tout en carbone permettent de réduire les vibrations et d’absorber les irrégularités de la route pour un plus grand confort du cycliste, et il y a de la place pour des pneus jusqu''à 32 mm. Et, avec un collier de serrage de tige de selle parfaitement intégré et des points de montage sous le tube supérieur, nous aimons à penser que nous avons pensé à tout... pour que vous ayez à faire soit rouler.', 'Le jury s''est prononcé et le verdict est simple : les freins à disque vous aident à aller plus vite. C''est pourquoi nous avons équipé l''Attain Pro de freins à disque mécaniques fiables et de pneus Continental Grand Prix 30 mm adhérents, afin que vous ayez toujours un contrôle total, quelle que soit la météo. Combiné à la transmission Claris 2x8 rapports fluide de Shimano, votre vitesse est littéralement à portée de main. Une fourche en carbone confortable et légère et un cadre conçu à la fois pour la vitesse et le confort constituent la touche finale. Quelle sera votre prochaine destination ce weekend?', 419, '2025-07-14', 3);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (13, 15, 5, 1, 1799.00, 63, 'ATTAIN C:62 - RACE', 'Performance et confort sont les deux faces d’une même pièce. Le cadre élégant et semi-aérodynamique de l''Attain C:62 est fabriqué à partir de notre carbone C:62© de qualité supérieure pour vous aider à fendre les airs. Il offre également un transfert de puissance efficace, grâce à ses axes traversants X12 et au profilage soigné du cadre, et le passage de câbles intégré réduit l''encombrement et offre un autre avantage aérodynamique. Tout a donc été pensé pour les performances. Côté confort, les Flex Stays et la fourche entièrement en carbone sont conçus pour réduire la fatigue des cyclistes, même les plus grands en taille ou roulant sur des surfaces routières plus accidentées, et le dégagement pour les pneus jusqu''à 34 mm se traduit par une grande adhérence et un amorti supplémentaire, si nécessaire.', 'Vous voulez de la vitesse, de la précision et du contrôle sans sacrifier le confort ? Le voici : l''Attain C:62 SLT. La vitesse est due à sa conception efficace et rehaussé sur le plan aérodynamique. Nous avons contribué à réduire la fatigue du cycliste grâce à des touches telles que la fourche en carbone, les Flex Stays et les pneus Continental tubeless-ready de 30 mm. La précision et le contrôle ? Ils sont assurés en grande partie par la transmission ultra-fiable Ultegra Di2 2x12 vitesses et les freins à disque hydrauliques de Shimano, qui offrent des performances impeccables même sous pression. Une paire de roues semi-aéro en carbone Newmen Advanced A.50 permet également de gagner quelques watts supplémentaires. Si l''on additionne tous ces éléments, la seule chose qui manque est un coureur prêt à faire les effort nécessaires. Êtes-vous prêt pour ça ?', 469, '2025-08-22', 2);
INSERT INTO public.velo_musculaire (id_article, id_millesime, id_modele_velo, id_materiau_cadre, prix_article, id_categorie, nom_article, description_article, resumer_article, nombre_vente_article, date_ajout, id_usage) VALUES (15, 2, 7, 1, 2649.00, 66, 'CROSS RACE C:62 - PRO', 'Voici ce qu''il faut savoir sur le cadre Cross Race C:62 : il présente plusieurs innovations identiques à notre cadre de cyclocross en carbone à la pointe de la technologie. Ainsi, par exemple, vous trouverez un collier de selle semi-intégré et le même passage de câbles interne qui s''insère parfaitement dans le haut du tube de tête, pour la même performance sans fouillis, une maintenance réduite et un aspect rangé. Fabriqué à partir de notre carbone C:62® à l''aide de notre technologie de fabrication Advanced Twin Mold, c''est le cadre de cyclocross performant qui élèvera votre course au niveau supérieur.', 'La course de cyclocross est dure - c''est en quelque sorte l’objectif complet de la course, après tout. C''est pourquoi, avec le Cross Race C:62 Pro, nous avons opté pour les vitesses 105 Di2 de Shimano - parce qu''elles ont la plage, la précision et la durabilité nécessaires pour gérer les courses difficiles dans la boue. Associé aux freins à disque Shimano 105 Di2 pour un contrôle de l''arrêt par tous les temps, c''est une combinaison imbattable. Mettez dans la course le cadre en carbone C:62 ® léger et spécifique cross ainsi que le pédalier Rotor Vegast BC2, et vous misez sur un vainqueur.', 716, '2025-06-26', 3);
