CREATE FUNCTION public.fn_create_bike(p_est_vae boolean, p_nom_article character varying, p_description_article text, p_resumer_article text, p_prix_article numeric, p_pourcentage_remise integer, p_id_categorie integer, p_id_modele_velo integer, p_id_materiau_cadre integer, p_id_millesime integer, p_id_usage integer, p_id_type_vae integer) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_id_article article.id_article%TYPE;
BEGIN
    -- 1. Insertion Article
        nom_article, 
        description_article, 
        resumer_article, 
        prix_article, 
        pourcentage_remise, 
        id_categorie
    ) VALUES (
        p_nom_article, 
        p_description_article, 
        p_resumer_article, 
        p_prix_article, 
        p_pourcentage_remise, 
        p_id_categorie
    ) RETURNING id_article INTO v_id_article;

    -- 2. Insertion Velo (Parent)
        id_article, 
        id_modele_velo, 
        id_categorie, 
        id_materiau_cadre, 
        id_millesime, 
        id_usage, 
        nom_article, 
        description_article, 
        resumer_article, 
        prix_article
    ) VALUES (
        v_id_article, 
        p_id_modele_velo, 
        p_id_categorie, 
        p_id_materiau_cadre, 
        p_id_millesime, 
        p_id_usage, 
        p_nom_article, 
        p_description_article, 
        p_resumer_article, 
        p_prix_article
    );

    -- 3. Insertion Enfants (Avec Duplication des données)
    IF p_est_vae THEN
            id_article, 
            id_type_vae,
            -- Champs dupliqués de Velo
            id_modele_velo,
            id_categorie,
            id_materiau_cadre,
            id_millesime,
            id_usage,
            nom_article,
            description_article,
            resumer_article,
            prix_article
        ) VALUES (
            v_id_article, 
            p_id_type_vae,
            p_id_modele_velo,
            p_id_categorie,
            p_id_materiau_cadre,
            p_id_millesime,
            p_id_usage,
            p_nom_article,
            p_description_article,
            p_resumer_article,
            p_prix_article
        );
    ELSE
            id_article,
            -- Champs dupliqués de Velo
            id_modele_velo,
            id_categorie,
            id_materiau_cadre,
            id_millesime,
            id_usage,
            nom_article,
            description_article,
            resumer_article,
            prix_article
        ) VALUES (
            v_id_article,
            p_id_modele_velo,
            p_id_categorie,
            p_id_materiau_cadre,
            p_id_millesime,
            p_id_usage,
            p_nom_article,
            p_description_article,
            p_resumer_article,
            p_prix_article
        );
    END IF;

    RETURN v_id_article;
END;
$$;


